export function Leaderboard({ leaderboards, currentUser, onBack }) {
    const [selectedCategory, setSelectedCategory] = useState('speed_4x4');

    const categories = [
        { id: 'speed_4x4', name: 'Speed - 4x4', icon: 'âš¡' },
        { id: 'speed_overall', name: 'Speed - Overall', icon: 'âš¡' },
        { id: 'moves_4x4', name: 'Moves - 4x4', icon: 'ðŸŽ¯' },
        { id: 'moves_overall', name: 'Moves - Overall', icon: 'ðŸŽ¯' }
    ];

    const currentLeaderboard = leaderboards[selectedCategory] || [];

    return (
        <div className="leaderboard-screen">
            <header className="screen-header">
                <button className="btn btn-secondary" onClick={onBack}>â† Back</button>
                <h1>ðŸ† Leaderboards</h1>
            </header>

            <div className="leaderboard-tabs">
                {categories.map(cat => (
                    <button
                        key={cat.id}
                        className={`tab ${selectedCategory === cat.id ? 'active' : ''}`}
                        onClick={() => setSelectedCategory(cat.id)}
                    >
                        {cat.icon} {cat.name}
                    </button>
                ))}
            </div>

            <div className="leaderboard-list">
                {currentLeaderboard.length === 0 ? (
                    <div className="empty-state">
                        <p>No rankings yet</p>
                    </div>
                ) : (
                    currentLeaderboard.map((entry, index) => (
                        <div
                            key={entry.user_id}
                            className={`leaderboard-entry ${entry.user_id === currentUser?.userId ? 'current-user' : ''}`}
                        >
                            <div className="entry-rank">
                                {index === 0 && 'ðŸ¥‡'}
                                {index === 1 && 'ðŸ¥ˆ'}
                                {index === 2 && 'ðŸ¥‰'}
                                {index > 2 && `#${index + 1}`}
                            </div>
                            <div className="entry-user">
                                <div className="user-name">{entry.display_name || entry.username}</div>
                                <div className="user-stats">ELO: {entry.elo_rating}</div>
                            </div>
                            <div className="entry-score">
                                {selectedCategory.includes('speed')
                                    ? `${entry.score}s`
                                    : `${entry.score} moves`}
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}

// ============================================================================
// USER PROFILE
// ============================================================================
export function UserProfile({ user, stats, achievements, onBack, onUpdateProfile }) {
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        displayName: user.displayName,
        bio: user.bio || ''
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        onUpdateProfile(formData);
        setIsEditing(false);
    };

    const completedAchievements = achievements?.filter(a => a.is_completed) || [];

    return (
        <div className="profile-screen">
            <header className="screen-header">
                <button className="btn btn-secondary" onClick={onBack}>â† Back</button>
                <h1>Profile</h1>
            </header>

            <div className="profile-content">
                <div className="profile-card">
                    <div className="profile-avatar">
                        {user.avatarUrl ? (
                            <img src={user.avatarUrl} alt={user.displayName} />
                        ) : (
                            <div className="avatar-placeholder">
                                {user.displayName.charAt(0).toUpperCase()}
                            </div>
                        )}
                    </div>

                    {!isEditing ? (
                        <>
                            <h2>{user.displayName}</h2>
                            <p className="username">@{user.username}</p>
                            {user.bio && <p className="bio">{user.bio}</p>}
                            <button className="btn btn-secondary" onClick={() => setIsEditing(true)}>
                                Edit Profile
                            </button>
                        </>
                    ) : (
                        <form onSubmit={handleSubmit} className="edit-form">
                            <input
                                type="text"
                                value={formData.displayName}
                                onChange={(e) => setFormData({ ...formData, displayName: e.target.value })}
                                placeholder="Display Name"
                            />
                            <textarea
                                value={formData.bio}
                                onChange={(e) => setFormData({ ...formData, bio: e.target.value })}
                                placeholder="Bio"
                                rows={3}
                            />
                            <div className="form-actions">
                                <button type="submit" className="btn btn-primary">Save</button>
                                <button type="button" className="btn btn-secondary" onClick={() => setIsEditing(false)}>
                                    Cancel
                                </button>
                            </div>
                        </form>
                    )}
                </div>

                <div className="profile-stats">
                    <h3>Statistics</h3>
                    <div className="stats-grid">
                        <div className="stat">
                            <div className="stat-value">{user.level}</div>
                            <div className="stat-label">Level</div>
                        </div>
                        <div className="stat">
                            <div className="stat-value">{stats?.total_games || 0}</div>
                            <div className="stat-label">Games</div>
                        </div>
                        <div className="stat">
                            <div className="stat-value">{stats?.total_wins || 0}</div>
                            <div className="stat-label">Wins</div>
                        </div>
                        <div className="stat">
                            <div className="stat-value">{stats?.elo_rating || 1000}</div>
                            <div className="stat-label">ELO</div>
                        </div>
                    </div>
                </div>

                <div className="profile-achievements">
                    <h3>Recent Achievements ({completedAchievements.length})</h3>
                    <div className="achievements-list">
                        {completedAchievements.slice(0, 6).map(ach => (
                            <div key={ach.achievement_id} className="achievement-badge">
                                ðŸŽ–ï¸ {ach.name}
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

// ============================================================================
// ACHIEVEMENT PANEL
// ============================================================================
export function AchievementPanel({ achievements, userAchievements, onBack }) {
    const categories = ['all', 'gameplay', 'speed', 'efficiency', 'competitive', 'special'];
    const [selectedCategory, setSelectedCategory] = useState('all');

    const filteredAchievements = selectedCategory === 'all'
        ? achievements
        : achievements.filter(a => a.category === selectedCategory);

    const getUserProgress = (achievementId) => {
        return userAchievements.find(ua => ua.achievement_id === achievementId);
    };

    return (
        <div className="achievements-screen">
            <header className="screen-header">
                <button className="btn btn-secondary" onClick={onBack}>â† Back</button>
                <h1>ðŸŽ–ï¸ Achievements</h1>
            </header>

            <div className="achievement-tabs">
                {categories.map(cat => (
                    <button
                        key={cat}
                        className={`tab ${selectedCategory === cat ? 'active' : ''}`}
                        onClick={() => setSelectedCategory(cat)}
                    >
                        {cat.charAt(0).toUpperCase() + cat.slice(1)}
                    </button>
                ))}
            </div>

            <div className="achievements-grid">
                {filteredAchievements.map(achievement => {
                    const progress = getUserProgress(achievement.achievement_id);
                    const isCompleted = progress?.is_completed || false;
                    const currentProgress = progress?.current_progress || 0;
                    const percentage = (currentProgress / achievement.requirement_value) * 100;

                    return (
                        <div
                            key={achievement.achievement_id}
                            className={`achievement-card ${isCompleted ? 'completed' : ''} rarity-${achievement.rarity}`}
                        >
                            <div className="achievement-icon">
                                {isCompleted ? 'âœ…' : 'ðŸ”’'}
                            </div>
                            <div className="achievement-info">
                                <h3>{achievement.name}</h3>
                                <p>{achievement.description}</p>
                                {!isCompleted && (
                                    <div className="progress-bar">
                                        <div
                                            className="progress-fill"
                                            style={{ width: `${percentage}%` }}
                                        ></div>
                                        <span className="progress-text">
                                            {currentProgress} / {achievement.requirement_value}
                                        </span>
                                    </div>
                                )}
                                {isCompleted && progress.unlocked_at && (
                                    <div className="unlock-date">
                                        Unlocked: {new Date(progress.unlocked_at).toLocaleDateString()}
                                    </div>
                                )}
                            </div>
                            <div className="achievement-reward">
                                +{achievement.xp_reward} XP
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}

