                    return (
                        <div key={powerup.powerup_id} className={`powerup-card rarity-${powerup.rarity}`}>
                            <div className="powerup-header">
                                <div className="powerup-icon-large">{getPowerupIcon(powerup.powerup_key)}</div>
                                <div className="powerup-quantity">Ã—{quantity}</div>
