}

function getPowerupIcon(key) {
    const icons = {
        'peek': 'ðŸ‘ï¸', 'hint': 'ðŸ’¡', 'shuffle_helper': 'ðŸ”„', 'time_freeze': 'â°',
        'auto_solve_3': 'ðŸŽ¯', 'undo_move': 'â†©ï¸', 'slow_motion': 'ðŸŒ', 'ghost_tile': 'ðŸ‘»'
    };
    return icons[key] || 'âœ¨';
}

// ============================================================================
// SETTINGS PANEL
// ============================================================================
export function SettingsPanel({ preferences, currentTheme, onUpdatePreferences, onChangeTheme, onBack, availableThemes }) {
    const [prefs, setPrefs] = useState(preferences || {});

    const handleSave = () => {
        onUpdatePreferences(prefs);
        onBack();
    };

    const themes = ['dasher', 'prancer', 'comet', 'blitzen', 'rudolph', 'aurora'];

    return (
        <div className="settings-screen">
            <header className="screen-header">
                <button className="btn btn-secondary" onClick={onBack}>â† Back</button>
                <h1>âš™ï¸ Settings</h1>
            </header>

            <div className="settings-content">
                <div className="settings-section">
                    <h2>Audio</h2>
                    <label className="setting-item">
                        <span>Music</span>
                        <input
                            type="checkbox"
                            checked={prefs.music_enabled}
                            onChange={(e) => setPrefs({ ...prefs, music_enabled: e.target.checked })}
                        />
                    </label>
                    <label className="setting-item">
                        <span>Sound Effects</span>
                        <input
                            type="checkbox"
                            checked={prefs.sound_enabled}
                            onChange={(e) => setPrefs({ ...prefs, sound_enabled: e.target.checked })}
                        />
                    </label>
                    <label className="setting-item">
                        <span>Music Volume</span>
                        <input
                            type="range"
                            min="0"
                            max="1"
                            step="0.1"
                            value={prefs.music_volume}
                            onChange={(e) => setPrefs({ ...prefs, music_volume: parseFloat(e.target.value) })}
                        />
                    </label>
                    <label className="setting-item">
                        <span>Sound Volume</span>
                        <input
                            type="range"
                            min="0"
                            max="1"
                            step="0.1"
                            value={prefs.sound_volume}
                            onChange={(e) => setPrefs({ ...prefs, sound_volume: parseFloat(e.target.value) })}
                        />
                    </label>
                </div>

                <div className="settings-section">
                    <h2>Gameplay</h2>
                    <label className="setting-item">
                        <span>Show Timer</span>
                        <input
                            type="checkbox"
                            checked={prefs.show_timer}
                            onChange={(e) => setPrefs({ ...prefs, show_timer: e.target.checked })}
                        />
                    </label>
                    <label className="setting-item">
                        <span>Show Move Counter</span>
                        <input
                            type="checkbox"
                            checked={prefs.show_move_counter}
                            onChange={(e) => setPrefs({ ...prefs, show_move_counter: e.target.checked })}
                        />
                    </label>
                    <label className="setting-item">
                        <span>Animations</span>
                        <input
                            type="checkbox"
                            checked={prefs.enable_animations}
                            onChange={(e) => setPrefs({ ...prefs, enable_animations: e.target.checked })}
                        />
                    </label>
                </div>

                <div className="settings-section">
                    <h2>Theme</h2>
                    <div className="theme-selector">
                        {themes.map(theme => (
                            <button
                                key={theme}
                                className={`theme-option ${currentTheme === theme ? 'selected' : ''} ${!availableThemes.includes(theme) ? 'locked' : ''}`}
                                onClick={() => availableThemes.includes(theme) && onChangeTheme(theme)}
                                disabled={!availableThemes.includes(theme)}
                            >
                                {theme.charAt(0).toUpperCase() + theme.slice(1)}
                                {!availableThemes.includes(theme) && ' ðŸ”’'}
                            </button>
                        ))}
                    </div>
                </div>

                <button className="btn btn-primary btn-large" onClick={handleSave}>
                    Save Settings
                </button>
            </div>
        </div>
    );
}

