              onChange={(e) =>
                setPrefs({ ...prefs, music_volume: parseFloat(e.target.value) })
              }
            />
          </label>
          <label className="setting-item">
            <span>Sound Volume</span>
            <input
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={prefs.sound_volume}
              onChange={(e) =>
                setPrefs({ ...prefs, sound_volume: parseFloat(e.target.value) })
              }
            />
